//Right Angled Triangle Number Pattern 

package Practisenumberpatternprogramming;

public class S4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n=5;
		for(int i=1;i<=n;i++) {
			for(int j=i;j<n;j++) {
				System.out.print("  ");
				
			}
			for(int k=1;k<=i;k++) {
				System.out.print("1 ");
			}
			System.out.println();
			
		}
		

		
		
	}

}
