//Decreasing angle triangle number pattern program 2

package Practisenumberpatternprogramming;

public class S7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5;
		for(int i=1;i<=n;i++) {
			for(int j=i;j<=n;j++) {
				System.out.print(i+" ");
			}
			System.out.println();
		}

	}

}
